Core:Properties.Core:Property:
         X[M] string* oldDescription;
string* newDescription;
int i;


if(\$(hook-oldvalue)) \{
    oldDescription = explode(\$(hook-oldvalue), " ");

    for(i = 0; i \< sizeof(oldDescription); i++) \{
        Set(\$this, "details:facialhair:sname:" + oldDescription[i], FALSE);
        Set(\$this, "details:facialhair:adjective:" + oldDescription[i], FALSE);
    \}

    Set(\$this, "details:facialhair:sname:facialhair", FALSE);
\}

if(\$(hook-value) \&\& \$(hook-value) != "none") \{
    newDescription = explode(\$(hook-value), " ");

    for(i = 0; i \< sizeof(newDescription); i++) \{
        Set(\$this, "details:facialhair:sname:" + newDescription[i], TRUE);
        Set(\$this, "details:facialhair:adjective:" + newDescription[i], TRUE);
    \}

    Set(\$this, "details:facialhair:sname:facialhair", TRUE);
\}

return TRUE;
