Core:Properties.Core:Property:
         X[M] /*
 * D=Look up an account's info.
 */
string acct;
object char;

if (!\$line) \{
    EmitTo(\$actor, PRE("Usage: +finger [character name]"));
    return nil;
\}

char = common::lookup(\$name: \$line);
if (!char) \{
    EmitTo(\$actor, "Character cannot be found.");
    return nil;
\}
acct = char."skotos:creator";
if (!acct) \{
    EmitTo(\$actor, "The character " + name(char) + " does not have a skotos:creator.\\nIn other words, that body is broken.");
    return nil;
\}

::lookup(\$acct: acct);

         X[M] /*
 * D=Look up information about \$acct and display to \$actor.
 *
 * Author: Kalle Alm
 * Email:  kalle@mortalis.skotos.net
 * Since:  Jan 22 12:33, 2006
 * State:  EXPERIMENTAL
 */
mapping info;
int tdays;
object *bodies;

info = juserapi::account_info(\$account: \$acct);

if (info == nil) \{
    EmitTo(\$actor, "Account does not exist.");
    return nil;
\}

if (catch(bodies = userapi::query_bodies_in_roster(\$user: \$acct))) \{
    EmitTo(\$actor, "That user either does not exist, or has never logged in.");
    return nil;
\}
tdays = ((time() - info["created"]) / 86400);

EmitTo(\$actor, PRE(
        "----- Finger Information -----\\n" +
        "Account name:       " + info["acct"] + "\\n" +
        "Email:              " + Str(info["email"]) + "\\n" +
        "Account status:     " + info["type"] + (info["host"] ? "; host" : "") + (info["banned"] ? "; banned" : "") + "\\n" +
        "---\\n" +
        (info["realname"] ? "Real name:          " + info["realname"] + "\\n" : "") +
        (info["birthday"] ? "Birthday:           " + info["birthday"] + "\\n" : "") +
        (info["gender"] ?   "Gender:             " + info["gender"] + "\\n"   : "") +
        "User status:        " + Str(info["status"]) + "\\n" +
        "--\\n" +
        "Created:            " + ctime(info["created"]) + "\\n" +
        "Characters played:  " + Describe(bodies) + "\\n" +
        "Last seen [text]:   " + info["lasttext"] + "\\n" +
        "Last seen [http]:   " + info["lasthttp"] + "\\n" +
        "--\\n" +
        "# of Days:          " + Int(info["days"]) + "\\n" +
        "Total Days:         " + tdays + "\\n" +
        "% of Days:          " + (tdays \> 0 ? format_float((Flt(info["days"])/(float)tdays)*100.0, 2) : "100") + "\\n"
    ));

/*
