         X[M] string desc;
object *inv;
object *haveIngredients;
int found,ctr,ctr2,ctr3;
string ingredientDescriptions;
string emitDescAct,emitDescEnv;


desc = Describe(this,\$actor,\$actor);
haveIngredients = (\{\});

/*
if(this."trait:isSealed")
\{
EmitTo(\$actor, desc + " has already been sealed.");
return FALSE;
\}
*/

if(!\$using)
\{
 EmitTo(\$actor,"You require " + Call(this,"describeSealingIngredients",\$sealingredients: this."trait:sealingredients") + "to seal " + desc + ".");
return FALSE;
\}

/*can I seal it anyway?*/
if (!this."trait:sealable")\{
 EmitTo (\$actor,"Unfortunately you cannot seal " + desc);
 return FALSE;
\}

/*check to see if you need to hold the item to seal it*/
if(this."trait:holdToSeal")\{
 Call(this,"gotsub");
 if(this.doexit) \{
  this.doexit = nil;
  return FALSE;
 \}
\}

/*loop through sealing ingredients to see what you need*/


if(this."trait:sealingredients")\{
 inv = \$using;

 found = 0;
  for(ctr = 0; ctr \< sizeof(inv); ctr++ )\{
  for(ctr2=0;ctr2\<sizeof(this."trait:sealingredients");ctr2++)\{
   if(Get(NRefOb(inv[ctr]),this."trait:sealingredients"[ctr2][0]))\{
    haveIngredients += (\{NRefOb(inv[ctr])\});
   \}
  \}
 \}

 if(sizeof(haveIngredients) != sizeof(this."trait:sealingredients"))\{
  EmitTo(\$actor,"You require " + Call(this,"describeSealingIngredients",\$sealingredients: this."trait:sealingredients") + "to seal " + desc + ".");
  return FALSE;
 \}
 else \{


/*ensure that inventory item is only refereced once*/
  for(ctr=0;ctr\<sizeof(haveIngredients);ctr++)\{
   for(ctr2=0;ctr2\<sizeof(haveIngredients);ctr2++)\{
    if(ctr != ctr2)\{
     if(haveIngredients[ctr] == haveIngredients[ctr2])\{
      for(ctr3=ctr2;ctr3\<sizeof(haveIngredients)-1;ctr3++)\{
       haveIngredients[ctr3]=haveIngredients[ctr3+1];
      \}
      haveIngredients[ctr3]=nil;
     \}
    \}
   \}
  \}

/*go through each ingredient item and call its 'being used as an ingredient'
script*/
  this.sealingDescriptions = (\{\});
  this.reusablesealingingredients = (\{\});
  this."trait:sealdescription" = "";
  emitDescAct="Using your ";
  emitDescEnv="Using " + \$actor."trait:possessive" + " ";

  for(ctr=0;ctr\<sizeof(haveIngredients);ctr++)\{
   if(haveIngredients[ctr])\{
    /*pass in this pointer so that items sealed description and re-spawn array can be modified*/
    if(FindMerry(haveIngredients[ctr],"lib","usedasingredient"))\{
     Call(haveIngredients[ctr],"usedasingredient",\$objSeal: this);
    \}
    if(Get(haveIngredients[ctr],"trait:superbrief"))\{
     emitDescAct += Get(haveIngredients[ctr],"trait:superbrief");
     emitDescEnv += Get(haveIngredients[ctr],"trait:superbrief");
    \}
    if(sizeof(haveIngredients) != 1)\{
     if(ctr == sizeof(haveIngredients)-2)\{
      emitDescAct += " and ";
      emitDescEnv += " and ";
     \}
     else \{
      if(ctr \< sizeof(haveIngredients)-1)\{
       emitDescAct += ", ";
       emitDescEnv += ", ";
      \}
     \}
    \}
   \}
  \}
 \}
\}
emitDescAct += " you seal " + desc + ".";
emitDescEnv += " " + \$actor."trait:brief" + " seals " + \$actor."trait:possessive" + " " + this."trait:superbrief" + ".";
/*do emits*/
EmitTo(\$actor,emitDescAct);
EmitIn(\$actor."base:environment",emitDescEnv,\$actor);


/*check sealed description*/
if(sizeof(this.sealingdescriptions) \> 0)\{
  this."trait:sealdescription" = "It has been sealed with ";
  for(ctr=0;ctr\<sizeof(this.sealingdescriptions);ctr++)\{
   this."trait:sealdescription" += this.sealingdescriptions[ctr];
   if(sizeof(this.sealingdescriptions) != 1)\{
     if(ctr == sizeof(this.sealingdescriptions) - 2)\{
       this."trait:sealdescription" += " and ";
     \}
     else \{
       if(ctr \< sizeof(this.sealingDescriptions) - 1)\{
         this."trait:sealdescription" += ", ";
       \}
     \}
   \}
  \}
  this."trait:sealdescription" += ".";
\}
else \{
 this."trait:sealdescription" = "It has been sealed.";
\}

this.sealingdescriptions = nil;

if(FindMerry(this,"lib","addsealingdetails"))\{
 Call(this,"addsealingdetails",\$ingredients: haveIngredients);
\}
Call(this,"descsub");
this."trait:isSealed" = "true";
