if (NRefDetail(\$target) == "limo") \{
    \$delay(0, nil, "84ff");
    Act(\$actor, "enter", \$what: NewNRef(this, "limo doors rear"));
    return nil;
\}
if (!\$actor."sys:tips:limo" \&\& NRefDetail(\$target) == "limo doors rear") \{
    \$delay(5, TRUE, "6ef9");
    if (\$actor."base:environment".homeowner == \$actor) \{
        EmitTo(\$actor, TAG("** TIP **\\nThe limousine is a virtual room, which means it is a \\"private location\\" for you. If you want to invite others into your limousine, you need to open the door and let them enter first, or they will simply end up in their own limousines.", "imp") + "\\n(This message appears only once.)");
        \$actor."sys:tips:limo" = TRUE;
    \}
\}
return TRUE;
