Core:PCProperties.Core:Property:
         X[M] object scroll;
string det, evoke;
int langlevel;

scroll = NRefOb(\$what[0]);
det = NRefDetail(\$what[0]);

Set(scroll,
    "lastsigner:object",
    \$actor);
Set(scroll,
    "details:" + det + ":descriptions:delivery-from",
    Describe(\$actor));
if (\$evoke) \{

  /* This code lets you write in languages - Aziel, 13, June 2004 */
  langlevel = ((\$r1 = \$actor."skotos:currentlang") ? Int(\$r1) : 0);

  if (langlevel \&\& langlevel != 0 \&\& strlen(\$(raw-evoke)) \> 0) \{
    evoke = INLANG(Get(\$\{Data:Languages:languages\}, "language:" + langlevel), Get(\$actor, "skill:lang" + langlevel),
            \$(raw-evoke));
  \} else \{
    evoke = \$(raw-evoke);
  \}
  Set(scroll,
      "details:" + det + ":descriptions:delivery-end",
      evoke);
\}
return TRUE;
