         X[M] /*
 * D=Show diff between objects \$a and \$b for \$media (which defaults to "ascii" but can also be "web"). Automatically excludes "revisions" and such. Returns result as a string.
 */
if (!\$a) error("\$a missing in call to ob_diff()");
if (!\$b) error("\$b missing in call to ob_diff()");

\{
        mapping a, b;
        string  *adet, *bdet, *list, r, p, aname, bname, s, dumpa, dumpb;
        int     i, ix, textmode;

        textmode = \$media != "web";

        /* Grab names. */
        aname = \$aref ? \$aref : name(\$a);
        bname = \$bref ? \$bref : name(\$b);
        /* Grab all accessible properties. */
        a = \$a."*";
        b = \$b."*";
        /* Rip out revisions, sys:sync:igdipro, sys:sync:source-server, and sys:sync:woename. */
        a -= (\{ "revisions", "sys:sync:igdipro", "sys:sync:source-server", "sys:sync:woename" \});
        b -= (\{ "revisions", "sys:sync:igdipro", "sys:sync:source-server", "sys:sync:woename" \});
        /* Add base:details array. */
        adet = \$a."base:details";
        bdet = \$b."base:details";
        a += ([ "base:details" : adet ]);
        b += ([ "base:details" : bdet ]);
        /*
         * Add each detail's properties. These include:
         *   "detail:descriptions"
         *   "detail:snames"
         *   "detail:pnames"
         *   "detail:adjectives"
         *   "detail:prepositions"
         */
        if (adet) a += ::ob_det_props(\$ob: \$a, \$details: adet);
        if (bdet) b += ::ob_det_props(\$ob: \$b, \$details: bdet);

        /* Compare properties. */
        list = map_indices(a) \| map_indices(b);
        ix   = sizeof(list);
        r    = "";
        for (i = 0; i \< ix; i++) \{
                p = list[i];
                dumpa = dump_value(a[p]);
                dumpb = dump_value(b[p]);
                s = (textmode ? TAG(p, "imp") : "\" + p + "\") + ": ";
                if (a[p] == nil \|\| b[p] == nil) \{
                        s += "only in " + (a[p] == nil ? bname : aname);
                \} else if (typeof(a[p]) != typeof(b[p])) \{
                        s += "type change from " + ascii_typeof(a[p]) + " to " + ascii_typeof(b[p]);
                \} else \{
                        if (strlen(dumpa) \> 3 \&\& dumpa[..3] == "X[S]") \{
                                a[p] = dumpa;
                                b[p] = dumpb;
                        \} else if (typeof(a[p]) == T_OBJECT \&\& a[p]."merry:source") \{
                                a[p] = a[p]."merry:source";
                                b[p] = b[p]."merry:source";
                        \}

                        switch (typeof(a[p])) \{
                        case T_INT:
                        case T_FLOAT:
                        case T_OBJECT:
                        case T_STRING:
                                if (a[p] == b[p]) \{
                                        s = nil;
                                        break;
                                \}
                                s += "\\n" + Call(textmode
                                                 ? ascii::
                                                 : html::,
                                                 "implode_diff",
                                                 \$diff: ascii::diff(\$data: Str(a[p]), \$datab: Str(b[p])));
                                break;

                        case T_ARRAY:
                                if (dump_value(a[p]) == dump_value(b[p])) \{
                                        s = nil;
                                \} else \{
                                        string *sa;

                                        s += "arrays differ: ";

                                        sa = a[p] - b[p];
                                        if (sizeof(sa)) s += "\\n--\> -" + dump_value(sa);
                                        sa = b[p] - a[p];
                                        if (sizeof(sa)) s += "\\n--\> +" + dump_value(sa);
                                \}
                                break;

                        case T_MAPPING:
                                if (dump_value(a[p]) == dump_value(b[p])) \{
                                        s = nil;
                                \} else \{
                                        mapping m;

                                        s += "mappings differ: ";
                                        m = a[p] - map_indices(b[p]);
                                        if (map_sizeof(m)) s += "\\n--\> -" + dump_value(m);
                                        m = b[p] - map_indices(a[p]);
                                        if (map_sizeof(m)) s += "\\n--\> +" + dump_value(m);
                                        s += "\\n(there may also be property differences)";
                                \}
                                break;
                        \}
                \}
                if (s) r += s + "\\n\\n";
        \}
        return r;
\}

