         X[M] /*
** Expects:
**
**   \$n - given such that 0 \<= return value \< \$n
**
** Source:
**
**   Stephen K. Park and Keith W. Miller, "Random Number
**   Generators: Good ones are hard to find",
**   Communications of the ACM, October 1988, vol 31,
**   number 10, pp. 1192-1201.
*/

int seed, high, low, test, final;

if (\$n \< 1) \{
  return 0;
\}
seed = this."rnd:seed";

high = seed / this."rnd:Q";
low  = seed % this."rnd:Q";
test = this."rnd:A" * low - this."rnd:R" * high;

seed = (test \> 0) ? test : test + this."rnd:M";

final = seed / (this."rnd:M"/\$n);

/* it should end here, were it not for sloppy arithmetic */

if (final \>= \$n) final = \$n-1; /* YUK! NOT RANDOM! */

this."rnd:seed" = seed;

return final;

         X[M] /*
** Expects:
**
**   \$word - the word to be summed
*/

int val, sum, i;

sum = 0;
for (i = 0; i \< strlen(\$word); i ++) \{
  /* evaluate how common this (downcased) letter is */
  switch(\$word[i] \| 0x20) \{
 case 'e': val = 1; break;
 case 't': val = 2; break;
 case 'a': val = 3; break;
 case 'i': val = 4; break;
 case 'n': val = 5; break;
 case 'o': val = 6; break;
 case 's': val = 7; break;
 case 'h': val = 8; break;
 case 'r': val = 9; break;
 case 'd': val = 10; break;
 case 'l': val = 11; break;
 case 'u': val = 12; break;
 case 'c': val = 13; break;
 case 'm': val = 14; break;
 case 'f': val = 15; break;
 case 'w': val = 16; break;
 case 'y': val = 17; break;
 case 'g': val = 18; break;
 case 'p': val = 19; break;
 case 'b': val = 20; break;
 case 'v': val = 21; break;
 case 'k': val = 22; break;
 case 'q': val = 23; break;
 case 'j': val = 24; break;
 case 'x': val = 25; break;
 case 'z': val = 26; break;
 case '0' .. '9': val = 5; break;
  \}
  sum += val;
\}
return sum;
