         X[M] int skill, lid;

lid = Call(this, "lang-to-lid", \$lang: \$language);

skill = Call(this, "get-skill", \$body: \$actor, \$lid: lid);

\$text = Call(this, "garble",
             \$lang: \$language, \$lvl: \$legibility * skill / 10);

return TRUE;

         X[M] /*  
** Expects:  
**  
**   \$text - the string to be garbled  
**   \$lang - the language identifier  
**   \$lvl  - the amount of garbling performed, from 0 to 10  
**  
*/  
  
string output, word;  
int seed, sum, begin, i;  
  
output = "";  
seed = 0;  
 
\$lvl *= 7; 
 
while (TRUE) \{  
  if (i \< strlen(\$text)) \{  
    switch(\$text[i]) \{  
      case 'a' .. 'z':  
      case 'A' .. 'Z':  
      case '0' .. '0':  
        if (!word) \{  
          /* this begins a new word */  
          begin = i;  
          seed = 0; 
        \} 
        word = \$text[begin .. i];  
        /* for some reason the whole word is summed */  
        sum = Call(this, "sum-word", \$word: word);  
        seed += 10 * (i+1 - begin) * sum; 
        i ++; 
        continue;  
    \}  
  \}  
  if (word) \{  
    /* we have a pending word; deal with it */  
    if (sum \< \$lvl) \{  
      /* the word is simple enough for garbling level \$lvl */  
      output += word;  
    \} else \{  
      /* or, if not, we garble it */  
      output += Call(this, "garbled-word", \$seed: seed, \$word: word);  
    \}  
    word = nil;  
  \}  
  if (i == strlen(\$text)) \{  
    /* OK: we're done */  
    return output;  
  \}  
  output += \$text[i .. i];  
  i ++;  
\}

         X[M] /*  
** Expects:  
**  
**   \$word - the language to garble in  
**   \$seed - the seed for the pseudo-random sequence  
*/  
  
string *syllables;  
string *out;  
int sylix, i;  
  
syllables = Call(this, "get-syllables", \$lang: \$lang);  
  
Call(this, "randomize", \$seed: \$seed);  
  
/* construct a word of 1-4 syllables */  
out = allocate(1 + Call(this, "random", \$n: 4));  
  
for (i = 0; i \< sizeof(out); i ++) \{  
  sylix = Call(this, "random", \$n: sizeof(syllables));  
  out[i] = syllables[sylix];  
\}  
/* mush the syllables together and return the result */  
return implode(out, "");

         X[M] /*
** Expects:
**
**   \$body - whose skill we're seeking
**   \$lid  - the Eric language id, e.g. 2 or '2'
**
*/

mixed skill;
skill = Get(\$body, "skill:lang" + Int(\$lid));
if (skill == nil) \{
  return 0;
\}
return Int(skill);
switch(Int(\$lid)) \{
 case 0: return "common";
 case 1: return "eastern";
 case 2: return "western";
 case 3: return "northern";
 case 4: return "toad";
 case 5: return "teanga";
 case 6: return "eme";
 case 7: return "cat";
 case 8: return "ibkhar";
\}
error("unknown language: " + dump_value(\$lid));

