Core:PCProperties.Core:Property:
         X[M] if (\$(new-light-category) != \$(old-light-category)) \{
  /* remember: \$actor is i.e. the container */ 
  EmitIn(\$actor, 
         "It's now " + \$(new-light-category) + 
         " here, rather than " + \$(old-light-category) + ".", 
         \$(light-bringer)); 
\} 
return TRUE;
